[text]
options {
    listen-on port 53 { localhost; };
    allow-query     { any; };
    directory       &quot;/opt/bind/var&quot;;
    recursion yes;
    allow-transfer { none; };
};
logging {
    channel default_debug {
        file &quot;/opt/bind/var/named.run&quot;;
        severity dynamic;
    };
    channel query_log {
        file &quot;/opt/bind/var/query.log&quot;;
        severity debug;
        print-time yes;
        print-category yes;
    };
    category queries{
        query_log;
    };
};
