[bash]

server {
 listen 80;
 listen 443 ssl;
 server_name ssl.humen1.net;

ssl_certificate /etc/letsencrypt/live/ssl.humen1.net/fullchain.pem;
 ssl_certificate_key /etc/letsencrypt/live/ssl.humen1.net/privkey.pem;

location / {
 root /data/letsencrypt;
 index index.html;
 }
}

[/bash]

